    @Override
    public void run() {
        while(true){
            try {
                client = server.accept();
                TicTacToe game = new TicTacToe(client);
                Thread t = new Thread(game);
                t.start();
                game.setVisible(true);
            } catch (IOException ex) {
                Logger.getLogger(ServerGame.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }
